import React, { useState } from 'react';
import { inventoryAPI } from '../services/api';

const AddFood = ({ user, onFoodAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    quantity: '',
    unit: 'kg',
    purchaseDate: '',
    expiryDate: '',
    storage: 'pantry',
    calories: '',
    category: 'other',
    notes: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const storageOptions = [
    { value: 'refrigerator', label: 'Refrigerator' },
    { value: 'freezer', label: 'Freezer' },
    { value: 'pantry', label: 'Pantry' },
    { value: 'counter', label: 'Counter' }
  ];

  const categoryOptions = [
    { value: 'fruits', label: 'Fruits' },
    { value: 'vegetables', label: 'Vegetables' },
    { value: 'dairy', label: 'Dairy' },
    { value: 'meat', label: 'Meat' },
    { value: 'grains', label: 'Grains' },
    { value: 'beverages', label: 'Beverages' },
    { value: 'other', label: 'Other' }
  ];

  const unitOptions = [
    { value: 'kg', label: 'Kilograms (kg)' },
    { value: 'g', label: 'Grams (g)' },
    { value: 'l', label: 'Liters (l)' },
    { value: 'ml', label: 'Milliliters (ml)' },
    { value: 'pieces', label: 'Pieces' },
    { value: 'packets', label: 'Packets' }
  ];

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Validate required fields
      if (!formData.name.trim()) {
        throw new Error('Food name is required');
      }
      if (!formData.quantity || parseFloat(formData.quantity) <= 0) {
        throw new Error('Please enter a valid quantity');
      }
      if (!formData.purchaseDate) {
        throw new Error('Purchase date is required');
      }
      if (!formData.expiryDate) {
        throw new Error('Expiry date is required');
      }

      // Validate dates
      const purchaseDate = new Date(formData.purchaseDate);
      const expiryDate = new Date(formData.expiryDate);
      
      if (expiryDate <= purchaseDate) {
        throw new Error('Expiry date must be after purchase date');
      }

      const foodData = {
        userId: user._id,
        name: formData.name.trim(),
        quantity: parseFloat(formData.quantity),
        unit: formData.unit,
        purchaseDate: formData.purchaseDate,
        expiryDate: formData.expiryDate,
        storage: formData.storage,
        calories: formData.calories ? parseInt(formData.calories) : 0,
        category: formData.category,
        notes: formData.notes.trim()
      };

      const response = await inventoryAPI.add(foodData);

      if (response.data.success) {
        setSuccess('Food item added successfully!');
        
        // Reset form
        setFormData({
          name: '',
          quantity: '',
          unit: 'kg',
          purchaseDate: '',
          expiryDate: '',
          storage: 'pantry',
          calories: '',
          category: 'other',
          notes: ''
        });

        // Notify parent component
        if (onFoodAdded) {
          onFoodAdded(response.data.foodItem);
        }

        // Clear success message after 3 seconds
        setTimeout(() => setSuccess(''), 3000);
      } else {
        throw new Error(response.data.message || 'Failed to add food item');
      }
    } catch (err) {
      console.error('Add food error:', err);
      setError(err.response?.data?.message || err.message || 'Failed to add food item. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Set default dates
  React.useEffect(() => {
    const today = new Date().toISOString().split('T')[0];
    const nextWeek = new Date();
    nextWeek.setDate(nextWeek.getDate() + 7);
    const nextWeekStr = nextWeek.toISOString().split('T')[0];

    if (!formData.purchaseDate) {
      setFormData(prev => ({ ...prev, purchaseDate: today }));
    }
    if (!formData.expiryDate) {
      setFormData(prev => ({ ...prev, expiryDate: nextWeekStr }));
    }
  }, []);

  return (
    <div className="main-content">
      <div className="container">
        <div className="card">
          <div className="card-header">
            <div>
              <h1 className="card-title">
                <i className="fas fa-plus-circle"></i>
                Add Food Item
              </h1>
              <div className="card-subtitle">
                Track your food inventory to reduce waste
              </div>
            </div>
          </div>

          {error && (
            <div className="error">
              <i className="fas fa-exclamation-circle"></i>
              {error}
            </div>
          )}

          {success && (
            <div className="success">
              <i className="fas fa-check-circle"></i>
              {success}
            </div>
          )}

          <form onSubmit={handleSubmit}>
            <div className="grid grid-2">
              <div className="form-group">
                <label className="form-label">Food Name *</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="e.g., Apples, Milk, Bread"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Category</label>
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  className="form-select"
                >
                  {categoryOptions.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Quantity *</label>
                <input
                  type="number"
                  name="quantity"
                  value={formData.quantity}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="e.g., 2.5"
                  step="0.1"
                  min="0"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Unit</label>
                <select
                  name="unit"
                  value={formData.unit}
                  onChange={handleChange}
                  className="form-select"
                >
                  {unitOptions.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Purchase Date *</label>
                <input
                  type="date"
                  name="purchaseDate"
                  value={formData.purchaseDate}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Expiry Date *</label>
                <input
                  type="date"
                  name="expiryDate"
                  value={formData.expiryDate}
                  onChange={handleChange}
                  className="form-input"
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Storage Location</label>
                <select
                  name="storage"
                  value={formData.storage}
                  onChange={handleChange}
                  className="form-select"
                >
                  {storageOptions.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">Calories (Optional)</label>
                <input
                  type="number"
                  name="calories"
                  value={formData.calories}
                  onChange={handleChange}
                  className="form-input"
                  placeholder="e.g., 150"
                  min="0"
                />
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">Notes (Optional)</label>
              <textarea
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                className="form-textarea"
                placeholder="Any additional notes about this food item..."
                rows="3"
              />
            </div>

            <div style={{ display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => {
                  setFormData({
                    name: '',
                    quantity: '',
                    unit: 'kg',
                    purchaseDate: '',
                    expiryDate: '',
                    storage: 'pantry',
                    calories: '',
                    category: 'other',
                    notes: ''
                  });
                  setError('');
                  setSuccess('');
                }}
              >
                <i className="fas fa-undo"></i>
                Reset Form
              </button>
              
              <button
                type="submit"
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <div className="loading-spinner" style={{ width: '20px', height: '20px' }}></div>
                    Adding...
                  </>
                ) : (
                  <>
                    <i className="fas fa-plus"></i>
                    Add Food Item
                  </>
                )}
              </button>
            </div>
          </form>

          <div style={{ marginTop: '24px', padding: '16px', background: '#f7fafc', borderRadius: '8px', fontSize: '0.9rem', color: '#4a5568' }}>
            <h4 style={{ marginBottom: '8px', color: '#2d3748' }}>
              <i className="fas fa-lightbulb"></i>
              Tips for better tracking:
            </h4>
            <ul style={{ paddingLeft: '20px', lineHeight: '1.6' }}>
              <li>Be accurate with expiry dates to get better waste prevention alerts</li>
              <li>Include calories to track nutritional value of saved food</li>
              <li>Use notes to remember specific details about the item</li>
              <li>Choose the right storage location for optimal freshness</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddFood;
