import React, { useState, useEffect } from 'react';
import { marketplaceAPI, inventoryAPI } from '../services/api';

const Marketplace = ({ user }) => {
  const [buyers, setBuyers] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [selectedTab, setSelectedTab] = useState('buyers');
  const [sellModal, setSellModal] = useState({ show: false, buyer: null, foodItem: null });
  const [sellForm, setSellForm] = useState({
    quantity: '',
    price: '',
    type: 'sell'
  });

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      const [buyersRes, inventoryRes, transactionsRes] = await Promise.all([
        marketplaceAPI.getBuyers(),
        inventoryAPI.list(user._id),
        marketplaceAPI.getTransactions(user._id)
      ]);

      if (buyersRes.data.success) setBuyers(buyersRes.data.buyers);
      if (inventoryRes.data.success) setInventory(inventoryRes.data.inventory);
      if (transactionsRes.data.success) setTransactions(transactionsRes.data.transactions);
    } catch (err) {
      console.error('Load data error:', err);
      setError('Failed to load marketplace data');
    } finally {
      setLoading(false);
    }
  };

  const handleSellClick = (buyer, foodItem) => {
    setSellModal({ show: true, buyer, foodItem });
    setSellForm({
      quantity: foodItem.quantity.toString(),
      price: (foodItem.quantity * 2).toFixed(2), // Default price: $2 per unit
      type: 'sell'
    });
  };

  const handleDonateClick = (buyer, foodItem) => {
    setSellModal({ show: true, buyer, foodItem });
    setSellForm({
      quantity: foodItem.quantity.toString(),
      price: '0',
      type: 'donate'
    });
  };

  const handleSellSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const quantity = parseFloat(sellForm.quantity);
      const price = parseFloat(sellForm.price);

      if (quantity <= 0 || quantity > sellModal.foodItem.quantity) {
        throw new Error('Invalid quantity');
      }

      if (sellForm.type === 'sell' && price <= 0) {
        throw new Error('Price must be greater than 0 for sales');
      }

      const transactionData = {
        sellerId: user._id,
        buyerId: sellModal.buyer._id,
        foodItemId: sellModal.foodItem._id,
        quantity,
        price,
        type: sellForm.type
      };

      const response = await marketplaceAPI.createTransaction(transactionData);

      if (response.data.success) {
        setSuccess(`${sellForm.type === 'sell' ? 'Sale' : 'Donation'} created successfully!`);
        setSellModal({ show: false, buyer: null, foodItem: null });
        loadData(); // Refresh data
        setTimeout(() => setSuccess(''), 3000);
      } else {
        throw new Error(response.data.message || 'Transaction failed');
      }
    } catch (err) {
      console.error('Transaction error:', err);
      setError(err.response?.data?.message || err.message || 'Transaction failed');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return '#f59e0b';
      case 'confirmed': return '#10b981';
      case 'completed': return '#3b82f6';
      case 'cancelled': return '#ef4444';
      default: return '#6b7280';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending': return 'fas fa-clock';
      case 'confirmed': return 'fas fa-check';
      case 'completed': return 'fas fa-check-double';
      case 'cancelled': return 'fas fa-times';
      default: return 'fas fa-question';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const availableInventory = (inventory || []).filter(item => 
    item.status === 'fresh' || item.status === 'expiring_soon'
  );

  return (
    <div className="main-content">
      <div className="container">
        <div className="card">
          <div className="card-header">
            <div>
              <h1 className="card-title">
                <i className="fas fa-store"></i>
                Marketplace
              </h1>
              <div className="card-subtitle">
                Connect with buyers to sell or donate your excess food
              </div>
            </div>
          </div>

          {error && (
            <div className="error">
              <i className="fas fa-exclamation-circle"></i>
              {error}
            </div>
          )}

          {success && (
            <div className="success">
              <i className="fas fa-check-circle"></i>
              {success}
            </div>
          )}

          {/* Tab Navigation */}
          <div style={{ 
            display: 'flex', 
            borderBottom: '2px solid #e5e7eb', 
            marginBottom: '24px',
            gap: '0'
          }}>
            {[
              { id: 'buyers', label: 'Find Buyers', icon: 'fas fa-users' },
              { id: 'inventory', label: 'My Inventory', icon: 'fas fa-boxes' },
              { id: 'transactions', label: 'My Transactions', icon: 'fas fa-exchange-alt' }
            ].map(tab => (
              <button
                key={tab.id}
                onClick={() => setSelectedTab(tab.id)}
                style={{
                  padding: '12px 24px',
                  border: 'none',
                  background: 'none',
                  cursor: 'pointer',
                  borderBottom: selectedTab === tab.id ? '2px solid #3b82f6' : '2px solid transparent',
                  color: selectedTab === tab.id ? '#3b82f6' : '#6b7280',
                  fontWeight: selectedTab === tab.id ? 'bold' : 'normal',
                  transition: 'all 0.2s'
                }}
              >
                <i className={tab.icon} style={{ marginRight: '8px' }}></i>
                {tab.label}
              </button>
            ))}
          </div>

          {loading && (
            <div style={{ textAlign: 'center', padding: '40px' }}>
              <div className="loading-spinner" style={{ width: '40px', height: '40px', margin: '0 auto 16px' }}></div>
              <p>Loading marketplace data...</p>
            </div>
          )}

          {/* Buyers Tab */}
          {selectedTab === 'buyers' && !loading && (
            <div>
              <div style={{ marginBottom: '20px' }}>
                <h3>Available Buyers</h3>
                <p style={{ color: '#6b7280' }}>
                  Connect with local buyers interested in purchasing or receiving food donations
                </p>
              </div>
              
              <div className="grid grid-2" style={{ gap: '20px' }}>
                {buyers.map(buyer => (
                  <div key={buyer._id} className="card" style={{ border: '1px solid #e5e7eb' }}>
                    <div style={{ padding: '20px' }}>
                      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '12px' }}>
                        <div style={{
                          width: '50px',
                          height: '50px',
                          borderRadius: '50%',
                          background: `linear-gradient(135deg, ${buyer.color || '#3b82f6'}, ${buyer.color || '#1d4ed8'})`,
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          color: 'white',
                          fontSize: '1.2rem',
                          fontWeight: 'bold',
                          marginRight: '12px'
                        }}>
                          {buyer.name.charAt(0).toUpperCase()}
                        </div>
                        <div>
                          <h4 style={{ margin: 0, color: '#1f2937' }}>{buyer.name}</h4>
                          <p style={{ margin: 0, color: '#6b7280', fontSize: '0.9rem' }}>
                            {buyer.type}
                          </p>
                        </div>
                      </div>

                      <div style={{ marginBottom: '12px' }}>
                        <p style={{ margin: '4px 0', fontSize: '0.9rem', color: '#4b5563' }}>
                          <i className="fas fa-map-marker-alt" style={{ marginRight: '6px', color: '#ef4444' }}></i>
                          {buyer.location}
                        </p>
                        <p style={{ margin: '4px 0', fontSize: '0.9rem', color: '#4b5563' }}>
                          <i className="fas fa-star" style={{ marginRight: '6px', color: '#f59e0b' }}></i>
                          {buyer.rating} ({buyer.reviews} reviews)
                        </p>
                      </div>

                      <div style={{ marginBottom: '16px' }}>
                        <p style={{ fontSize: '0.9rem', color: '#6b7280', margin: 0 }}>
                          <strong>Interested in:</strong> {(buyer.categories || []).join(', ')}
                        </p>
                      </div>

                      {availableInventory.length > 0 ? (
                        <div style={{ display: 'flex', gap: '8px' }}>
                          <button
                            onClick={() => handleSellClick(buyer, availableInventory[0])}
                            className="btn btn-primary"
                            style={{ flex: 1, fontSize: '0.9rem' }}
                          >
                            <i className="fas fa-dollar-sign"></i>
                            Sell
                          </button>
                          <button
                            onClick={() => handleDonateClick(buyer, availableInventory[0])}
                            className="btn btn-secondary"
                            style={{ flex: 1, fontSize: '0.9rem' }}
                          >
                            <i className="fas fa-heart"></i>
                            Donate
                          </button>
                        </div>
                      ) : (
                        <p style={{ color: '#9ca3af', fontSize: '0.9rem', textAlign: 'center', margin: 0 }}>
                          No available inventory to sell
                        </p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Inventory Tab */}
          {selectedTab === 'inventory' && !loading && (
            <div>
              <div style={{ marginBottom: '20px' }}>
                <h3>Available Inventory</h3>
                <p style={{ color: '#6b7280' }}>
                  Food items that can be sold or donated in the marketplace
                </p>
              </div>

              {availableInventory.length > 0 ? (
                <div className="grid grid-3" style={{ gap: '16px' }}>
                  {availableInventory.map(item => {
                    const isExpiringSoon = new Date(item.expiryDate) <= new Date(Date.now() + 3 * 24 * 60 * 60 * 1000);
                    const daysUntilExpiry = Math.ceil((new Date(item.expiryDate) - new Date()) / (1000 * 60 * 60 * 24));
                    
                    return (
                      <div key={item._id} className="card" style={{ border: '1px solid #e5e7eb' }}>
                        <div style={{ padding: '16px' }}>
                          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '8px' }}>
                            <h4 style={{ margin: 0, color: '#1f2937' }}>{item.name}</h4>
                            <div style={{
                              display: 'inline-block',
                              padding: '4px 8px',
                              borderRadius: '12px',
                              fontSize: '0.8rem',
                              fontWeight: 'bold',
                              backgroundColor: item.status === 'fresh' ? '#ecfdf5' : '#fef3c7',
                              color: item.status === 'fresh' ? '#065f46' : '#92400e'
                            }}>
                              {item.status === 'fresh' ? 'Fresh' : 'Expiring Soon'}
                            </div>
                          </div>
                          
                          <div style={{ marginBottom: '8px' }}>
                            <p style={{ margin: '0 0 4px 0', color: '#6b7280', fontSize: '0.9rem' }}>
                              <i className="fas fa-weight" style={{ marginRight: '6px', color: '#9ca3af' }}></i>
                              {item.quantity} {item.unit}
                            </p>
                            <p style={{ margin: '0 0 4px 0', color: '#6b7280', fontSize: '0.9rem' }}>
                              <i className="fas fa-tag" style={{ marginRight: '6px', color: '#9ca3af' }}></i>
                              {item.category}
                            </p>
                            <p style={{ margin: '0 0 4px 0', color: '#6b7280', fontSize: '0.9rem' }}>
                              <i className="fas fa-box" style={{ marginRight: '6px', color: '#9ca3af' }}></i>
                              {item.storage}
                            </p>
                          </div>
                          
                          <div style={{ marginBottom: '12px' }}>
                            <p style={{ 
                              margin: 0, 
                              fontSize: '0.8rem', 
                              color: isExpiringSoon ? '#dc2626' : '#059669',
                              fontWeight: '500'
                            }}>
                              <i className={`fas ${isExpiringSoon ? 'fa-exclamation-triangle' : 'fa-calendar-check'}`} 
                                 style={{ marginRight: '6px' }}></i>
                              {daysUntilExpiry > 0 
                                ? `Expires in ${daysUntilExpiry} day${daysUntilExpiry !== 1 ? 's' : ''}`
                                : 'Expires today'
                              }
                            </p>
                            <p style={{ margin: '4px 0 0 0', fontSize: '0.8rem', color: '#9ca3af' }}>
                              {new Date(item.expiryDate).toLocaleDateString()}
                            </p>
                          </div>
                          
                          {item.calories > 0 && (
                            <p style={{ margin: '0 0 8px 0', fontSize: '0.8rem', color: '#6b7280' }}>
                              <i className="fas fa-fire" style={{ marginRight: '6px', color: '#f59e0b' }}></i>
                              {item.calories} calories
                            </p>
                          )}
                          
                          {item.notes && (
                            <p style={{ 
                              margin: '8px 0 0 0', 
                              fontSize: '0.8rem', 
                              color: '#6b7280',
                              fontStyle: 'italic',
                              padding: '8px',
                              backgroundColor: '#f9fafb',
                              borderRadius: '4px'
                            }}>
                              "{item.notes}"
                            </p>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div style={{ textAlign: 'center', padding: '40px', color: '#9ca3af' }}>
                  <i className="fas fa-box-open" style={{ fontSize: '48px', marginBottom: '16px' }}></i>
                  <p style={{ margin: 0, fontSize: '1.1rem' }}>
                    No available inventory for marketplace
                  </p>
                  <p style={{ margin: '8px 0 0 0', fontSize: '0.9rem' }}>
                    Add fresh food items to start selling or donating
                  </p>
                </div>
              )}
            </div>
          )}

          {/* Transactions Tab */}
          {selectedTab === 'transactions' && !loading && (
            <div>
              <div style={{ marginBottom: '20px' }}>
                <h3>My Transactions</h3>
                <p style={{ color: '#6b7280' }}>
                  Track your sales and donations history
                </p>
              </div>

              {transactions.length > 0 ? (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
                  {transactions.map(transaction => (
                    <div key={transaction._id} className="card" style={{ border: '1px solid #e5e7eb' }}>
                      <div style={{ padding: '20px' }}>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '12px' }}>
                          <div>
                            <h4 style={{ margin: '0 0 4px 0', color: '#1f2937' }}>
                              {transaction.foodItem?.name || 'Food Item'}
                            </h4>
                            <p style={{ margin: 0, color: '#6b7280', fontSize: '0.9rem' }}>
                              {transaction.type === 'sell' ? 'Sale' : 'Donation'} to {transaction.buyer?.name || 'Buyer'}
                            </p>
                          </div>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                            <div style={{
                              display: 'flex',
                              alignItems: 'center',
                              padding: '4px 8px',
                              borderRadius: '12px',
                              fontSize: '0.8rem',
                              fontWeight: 'bold',
                              backgroundColor: getStatusColor(transaction.status) + '20',
                              color: getStatusColor(transaction.status)
                            }}>
                              <i className={getStatusIcon(transaction.status)} style={{ marginRight: '4px' }}></i>
                              {transaction.status}
                            </div>
                          </div>
                        </div>

                        <div className="grid grid-4" style={{ gap: '16px', marginBottom: '12px' }}>
                          <div>
                            <p style={{ margin: '0 0 4px 0', fontSize: '0.8rem', color: '#9ca3af' }}>Quantity</p>
                            <p style={{ margin: 0, fontWeight: 'bold', color: '#1f2937' }}>
                              {transaction.quantity} {transaction.foodItem?.unit || 'units'}
                            </p>
                          </div>
                          <div>
                            <p style={{ margin: '0 0 4px 0', fontSize: '0.8rem', color: '#9ca3af' }}>Price</p>
                            <p style={{ margin: 0, fontWeight: 'bold', color: '#1f2937' }}>
                              ${transaction.price.toFixed(2)}
                            </p>
                          </div>
                          <div>
                            <p style={{ margin: '0 0 4px 0', fontSize: '0.8rem', color: '#9ca3af' }}>Green Coins</p>
                            <p style={{ margin: 0, fontWeight: 'bold', color: '#10b981' }}>
                              +{transaction.greenCoinsEarned || 0}
                            </p>
                          </div>
                          <div>
                            <p style={{ margin: '0 0 4px 0', fontSize: '0.8rem', color: '#9ca3af' }}>Date</p>
                            <p style={{ margin: 0, fontWeight: 'bold', color: '#1f2937' }}>
                              {formatDate(transaction.createdAt)}
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div style={{ textAlign: 'center', padding: '40px', color: '#9ca3af' }}>
                  <i className="fas fa-receipt" style={{ fontSize: '48px', marginBottom: '16px' }}></i>
                  <p style={{ margin: 0, fontSize: '1.1rem' }}>
                    No transactions yet
                  </p>
                  <p style={{ margin: '8px 0 0 0', fontSize: '0.9rem' }}>
                    Start selling or donating food to see your transaction history
                  </p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Sell/Donate Modal */}
      {sellModal.show && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div className="card" style={{ width: '500px', maxWidth: '90vw' }}>
            <div style={{ padding: '24px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                <h3 style={{ margin: 0, color: '#1f2937' }}>
                  {sellForm.type === 'sell' ? 'Sell Food Item' : 'Donate Food Item'}
                </h3>
                <button
                  onClick={() => setSellModal({ show: false, buyer: null, foodItem: null })}
                  style={{ background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer', color: '#6b7280' }}
                >
                  
                </button>
              </div>

              <form onSubmit={handleSellSubmit}>
                <div style={{ marginBottom: '16px', padding: '12px', backgroundColor: '#f9fafb', borderRadius: '8px' }}>
                  <p style={{ margin: '0 0 4px 0', fontSize: '0.9rem', color: '#6b7280' }}>
                    <strong>Item:</strong> {sellModal.foodItem?.name}
                  </p>
                  <p style={{ margin: '0 0 4px 0', fontSize: '0.9rem', color: '#6b7280' }}>
                    <strong>Buyer:</strong> {sellModal.buyer?.name}
                  </p>
                  <p style={{ margin: 0, fontSize: '0.9rem', color: '#6b7280' }}>
                    <strong>Available:</strong> {sellModal.foodItem?.quantity} {sellModal.foodItem?.unit}
                  </p>
                </div>

                <div className="form-group">
                  <label className="form-label">Quantity</label>
                  <input
                    type="number"
                    value={sellForm.quantity}
                    onChange={(e) => setSellForm({ ...sellForm, quantity: e.target.value })}
                    className="form-input"
                    max={sellModal.foodItem?.quantity}
                    min="0.1"
                    step="0.1"
                    required
                  />
                </div>

                {sellForm.type === 'sell' && (
                  <div className="form-group">
                    <label className="form-label">Price ($)</label>
                    <input
                      type="number"
                      value={sellForm.price}
                      onChange={(e) => setSellForm({ ...sellForm, price: e.target.value })}
                      className="form-input"
                      min="0.01"
                      step="0.01"
                      required
                    />
                  </div>
                )}

                <div style={{ display: 'flex', gap: '12px', justifyContent: 'flex-end' }}>
                  <button
                    type="button"
                    onClick={() => setSellModal({ show: false, buyer: null, foodItem: null })}
                    className="btn btn-secondary"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <div className="loading-spinner" style={{ width: '20px', height: '20px' }}></div>
                        Processing...
                      </>
                    ) : (
                      <>
                        <i className={sellForm.type === 'sell' ? 'fas fa-dollar-sign' : 'fas fa-heart'}></i>
                        {sellForm.type === 'sell' ? 'Create Sale' : 'Create Donation'}
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Marketplace;
